import { Injectable } from '@angular/core';
import { CanActivate, ActivatedRouteSnapshot, RouterStateSnapshot, Router } from '@angular/router';

@Injectable({
  providedIn: 'root'
})
export class RoleGuard implements CanActivate {
  constructor(private router: Router) {}

  canActivate(
    route: ActivatedRouteSnapshot,
    state: RouterStateSnapshot
  ): boolean {
    // 1. Get the array of allowed roles from the route's data property
    const expectedRoles = route.data['roles'] as Array<string>;
    if (!expectedRoles || expectedRoles.length === 0) {
      console.error('[ROLE_GUARD] Route is missing required roles in its data property.');
      this.router.navigate(['/auth/login']);
      return false;
    }

    // 2. Get the current user's info from localStorage
    const userInfoString = localStorage.getItem('userInfo');
    if (!userInfoString) {
      console.error('[ROLE_GUARD] User info not found in localStorage. Redirecting to login.');
      this.router.navigate(['/auth/login']);
      return false;
    }

    // 3. Parse the user info and get their role
    const user = JSON.parse(userInfoString);
    const userRole = user.role;

    console.log(`[ROLE_GUARD] Checking access for route: ${state.url}`);
    console.log(`[ROLE_GUARD] Required roles: [${expectedRoles.join(', ')}], User's role: '${userRole}'`);

    // 4. Check if the user's role is in the list of expected roles
    if (expectedRoles.includes(userRole)) {
      console.log('[ROLE_GUARD] Role is authorized. Access GRANTED.');
      return true; // The user's role is in the allowed list, grant access
    } else {
      console.error(`[ROLE_GUARD] Access DENIED. User with role '${userRole}' cannot access this route.`);
      // If roles do not match, redirect to the main login page
      this.router.navigate(['/auth/login']); 
      return false;
    }
  }
}
