import { Injectable } from '@angular/core';
import { CanActivate, ActivatedRouteSnapshot, RouterStateSnapshot, Router } from '@angular/router';

@Injectable({
  providedIn: 'root'
})
export class RoleGuard implements CanActivate {
  constructor(private router: Router) {}

  canActivate(
    route: ActivatedRouteSnapshot,
    state: RouterStateSnapshot
  ): boolean {
    // Get the role required for this route from the 'data' property in the routing module
    const expectedRole = route.data['role']; 
    if (!expectedRole) {
      // If no role is specified in the route, something is misconfigured. Deny access.
      console.error('[ROLE_GUARD] Route is missing the expected role in its data property.');
      return false;
    }

    // Get the current user's info from localStorage
    const userInfoString = localStorage.getItem('userInfo');
    if (!userInfoString) {
      console.error('[ROLE_GUARD] User info not found in localStorage. Redirecting to login.');
      this.router.navigate(['/auth/login']);
      return false;
    }

    // Parse the user info and get the role
    const user = JSON.parse(userInfoString);
    const userRole = user.role; // The user object from your backend has a 'role' property

    console.log(`[ROLE_GUARD] Checking access. Required role: '${expectedRole}', User's role: '${userRole}'`);

    // If expected role is 'project_manager' we allow access if the user is either
    // - globally a project_manager, OR
    // - is project manager on at least one assigned project (stored in localStorage by login)
    if (expectedRole === 'project_manager') {
      if (userRole === 'project_manager') {
        console.log('[ROLE_GUARD] User has global project_manager role. Access granted.');
        return true;
      }

      // Check assigned projects for project manager assignment
      const assignedProjectsString = localStorage.getItem('assignedProjects') || '[]';
      let assignedProjects = [];
      try { assignedProjects = JSON.parse(assignedProjectsString); } catch(e) { assignedProjects = []; }
      // Support different shapes: project.projectManager.id, project.projectManagerId, project.project_manager_id
      const isPmOnProject = Array.isArray(assignedProjects) && assignedProjects.some((p: any) => {
        if (!p) return false;
        const pm = p.projectManager || p.project_manager || null;
        if (pm && (pm.id === user.id || pm === user.id)) return true;
        if (p.projectManagerId && p.projectManagerId === user.id) return true;
        if (p.project_manager_id && p.project_manager_id === user.id) return true;
        // some responses may include manager as string id directly
        if (p.projectManager === user.id) return true;
        return false;
      });
      if (isPmOnProject) {
        console.log('[ROLE_GUARD] User is project manager on at least one project. Access granted.');
        return true;
      }
    }

    // Default behavior: require exact role match
    if (userRole === expectedRole) {
      console.log('[ROLE_GUARD] Role matches. Access granted.');
      return true;
    }

    console.error(`[ROLE_GUARD] Access DENIED. User with role '${userRole}' cannot access the '${expectedRole}' route.`);
    // If roles do not match, redirect to the main login page
    this.router.navigate(['/auth/login']); 
    return false;
  }
}
