      color: 'green'
    };
    if (endDate < this.TODAY) return {
      label: 'Overdue',
      color: 'red'
    };
    const sevenDaysFromNow = new Date(this.TODAY);
    sevenDaysFromNow.setDate(this.TODAY.getDate() + 7);
    if (endDate <= sevenDaysFromNow) return {
