          incompleteTasks.forEach(task => {
            const status = this.getTaskStatus(task);
            if (status.label === 'Overdue') isOverdue = true;
            if (status.label === 'At-Risk') isAtRisk = true;
          });
        }
        let status = {
          label: 'On Track',
          color: 'green'
