        color: 'green'
      };else if (hasLaggingStage || p.stages.some(s => s.status?.label === 'Overdue')) overallStatus = {
        label: 'Lagging',
        color: 'red'
      };else if (p.stages.some(s => s.status?.label === 'At-Risk')) overallStatus = {
