import { Injectable } from '@angular/core';
import { CanActivate, Router } from '@angular/router';
import { AuthService } from './auth.service'; // Your auth service

@Injectable({
  providedIn: 'root'
})
export class AdminGuard implements CanActivate {

  constructor(private authService: AuthService, private router: Router) {}

  canActivate(): boolean {
    // --- ADD THESE LOGS ---
    console.log('[ADMIN_GUARD] Running canActivate check...');

    const userRole = this.authService.getUserRole(); // A function to get the role from localStorage or a service
    console.log('[ADMIN_GUARD] Current user role is:', userRole);

    if (userRole === 'admin') {
      console.log('[ADMIN_GUARD] Access GRANTED.');
      return true; // Allow navigation
    } else {
      console.error('[ADMIN_GUARD] Access DENIED. Navigating to home.');
      this.router.navigate(['/']); // Or some other page
      return false; // Block navigation
    }
  }
}
