// src/app/app-routing.module.ts

import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { AdminDashboardComponent } from './admin/admin-dashboard/admin-dashboard.component';
import { UserDashboardComponent } from './user/user-dashboard/user-dashboard.component';
import { LoginComponent } from './auth/login/login.component';
import { RegisterComponent } from './auth/register/register.component';
import { AuthGuard } from './shared/guards/auth.guard';
import { RoleGuard } from './shared/guards/role.guard'; // <-- IMPORT THE NEW GUARD

const routes: Routes = [
  { path: '', redirectTo: '/auth/login', pathMatch: 'full' },
  { path: 'auth/login', component: LoginComponent },
  { path: 'auth/register', component: RegisterComponent },
  { 
    path: 'admin', 
    component: AdminDashboardComponent, 
    canActivate: [AuthGuard, RoleGuard], // <-- USE BOTH GUARDS
    data: { role: 'admin' }               // <-- PROVIDE THE REQUIRED ROLE
  },
  { 
    path: 'user', 
    component: UserDashboardComponent, 
    canActivate: [AuthGuard, RoleGuard], // <-- USE BOTH GUARDS
    data: { role: 'project_manager' }      // <-- EXAMPLE: or any other non-admin role
  },
  { path: '**', redirectTo: '/auth/login' }
];

@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule]
})
export class AppRoutingModule { }
