// src/app/shared/guards/role.guard.ts

import { Injectable } from '@angular/core';
import { CanActivate, ActivatedRouteSnapshot, RouterStateSnapshot, Router } from '@angular/router';

@Injectable({
  providedIn: 'root'
})
export class RoleGuard implements CanActivate {
  constructor(private router: Router) {}

  canActivate(
    route: ActivatedRouteSnapshot,
    state: RouterStateSnapshot
  ): boolean {
    // 1. Get the role required for this route from the 'data' property
    const expectedRole = route.data['role']; 
    if (!expectedRole) {
      // If no role is specified in the route, allow access by default
      return true;
    }

    // 2. Get the current user's info from localStorage
    const userInfo = localStorage.getItem('userInfo');
    if (!userInfo) {
      console.error('[ROLE_GUARD] User info not found. Redirecting to login.');
      this.router.navigate(['/auth/login']);
      return false;
    }

    // 3. Parse the user info and get the role
    const user = JSON.parse(userInfo);
    const userRole = user.userRole; // Based on your log: { userRole: 'admin' }

    console.log(`[ROLE_GUARD] Required role: ${expectedRole}, User's role: ${userRole}`);

    // 4. Compare the roles
    if (userRole === expectedRole) {
      console.log('[ROLE_GUARD] Access GRANTED.');
      return true; // The user has the correct role, allow access
    } else {
      console.error(`[ROLE_GUARD] Access DENIED. User with role '${userRole}' cannot access '${expectedRole}' route.`);
      // Optional: redirect to a generic dashboard or an 'unauthorized' page
      this.router.navigate(['/']); 
      return false;
    }
  }
}
