  };
  constructor(sanitizer, projectService, router, authService) {
    this.sanitizer = sanitizer;
    this.projectService = projectService;
    this.router = router;
    this.authService = authService;
  }
  logout() {
    this.authService.logout();
    this.router.navigateByUrl('/auth/login');
  }
  ngOnInit() {
    this.loadUser();
    this.loadProjects();
    this.loadAvailableUsers();
  }
  loadUser() {
    this.authService.currentUser.subscribe(user => {
      if (user) {
        this.currentUser = user;
      } else {
        this.router.navigate(['/auth/login']);
      }
    });
  }
  loadProjects() {
    if (!this.currentUser) return;
    // Fetch ALL projects for transparency as requested
    this.projectService.getAllProjectsForDashboard().subscribe({
      next: projects => {
        this.allProjects = projects;
        this.splitProjects();
        this.calculateAllProjectsHealth();
      },
      error: err => console.error('Failed to load projects', err)
    });
  }
  loadAvailableUsers() {
    this.projectService.getAvailableManagers().subscribe({
      next: users => {
        this.availableUsers = users;
        console.log('Loaded available users for assignment:', this.availableUsers.length);
      },
      error: err => console.error('Failed to load users', err)
    });
  }
  splitProjects() {
    if (!this.currentUser) {
      return;
    }
    // Managed Projects: Where user is the Project Manager
    this.managedProjects = this.allProjects.filter(p => {
      const isManager = p.projectManager?.id === this.currentUser?.id;
      return isManager;
    });
    // Assigned Projects: Where user is assigned to stages or tasks (but not PM)
    this.assignedProjects = this.allProjects.filter(p => {
      if (p.projectManager?.id === this.currentUser?.id) return false; // Skip if PM
      // Check if assigned to any stage
      const assignedToStage = p.stages?.some(s => s.assignedTeamMembers?.some(u => u.id === this.currentUser?.id));
      // Check if assigned to any task
      const assignedToTask = p.stages?.some(s => s.tasks?.some(t => t.assignedTeamMembers?.some(u => u.id === this.currentUser?.id)));
      const isAssigned = assignedToStage || assignedToTask;
      return isAssigned;
    });
  }
  // --- UI Actions ---
  getIcon(name) {
    return this.sanitizer.bypassSecurityTrustHtml(this.ICONS_SVG[name] || '');
  }
  navigateTo(view, projectId) {
    if (projectId) {
      this.router.navigate(['/projects', projectId]);
      return;
    }
    this.currentView = view;
  }
  selectProject(project) {
    this.selectedProject = project;
  }
  toggleDarkMode() {
    this.isDarkMode = !this.isDarkMode;
    if (this.isDarkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }
  // --- Task Creation UI ---
  showTaskCreationModal = false;
  selectedStageForTask = null;
  newTaskName = '';
  newTaskStartDate = '';
  newTaskEndDate = '';
  openTaskCreationModal(stage) {
