class ProjectDetailComponent {
  projectService;
  taskService;
  project;
  availableManagers = [];
  currentUser = null;
  projectUpdated = new EventEmitter();
  projectDeleted = new EventEmitter();
  taskModalRequested = new EventEmitter();
  isEditingName = false;
  editedProjectName = '';
  selectedManagerId = '';
  constructor(projectService, taskService) {
    this.projectService = projectService;
    this.taskService = taskService;
  }
  ngOnInit() {
    this.editedProjectName = this.project.name;
  }
  startEditingName() {
    this.isEditingName = true;
    this.editedProjectName = this.project.name;
  }
  saveProjectName() {
    if (this.editedProjectName.trim() && this.editedProjectName !== this.project.name) {
      this.projectService.updateProject(this.project.id, {
        name: this.editedProjectName
      }).subscribe({
        next: updatedProject => {
          this.project = updatedProject;
          this.projectUpdated.emit(updatedProject);
          this.isEditingName = false;
        },
        error: error => console.error('Error updating project name:', error)
      });
    } else {
      this.isEditingName = false;
    }
  }
  cancelEditingName() {
    this.isEditingName = false;
    this.editedProjectName = this.project.name;
  }
  onManagerSelected(event) {
    const managerId = event.target.value;
    if (managerId) {
      this.assignProjectManager(managerId);
    }
  }
  assignProjectManager(managerId) {
    this.projectService.assignProjectManager(this.project.id, managerId).subscribe({
      next: updatedProject => {
        this.project = updatedProject;
        this.projectUpdated.emit(updatedProject);
        this.selectedManagerId = '';
      },
      error: error => console.error('Error assigning manager:', error)
    });
  }
  deleteProject() {
    if (confirm('Are you sure you want to delete this project? This action cannot be undone.')) {
      this.projectService.deleteProject(this.project.id).subscribe({
        next: () => {
          this.projectDeleted.emit(this.project.id);
        },
        error: error => console.error('Error deleting project:', error)
      });
    }
  }
  archiveProject() {
    if (confirm('Are you sure you want to archive this project?')) {
      this.projectService.updateProject(this.project.id, {
        archived: true
      }).subscribe({
        next: updatedProject => {
          this.project = updatedProject;
          this.projectUpdated.emit(updatedProject);
        },
        error: error => console.error('Error archiving project:', error)
      });
    }
  }
  toggleStage(stage) {
    stage.isOpen = !stage.isOpen;
  }
  openTaskModal(stageId) {
    this.taskModalRequested.emit(stageId);
  }
  assignUserToStage(stage, event) {
    const userId = event.target.value;
    if (userId) {
      // Implementation for assigning user to stage
      console.log(`Assign user ${userId} to stage ${stage.id}`);
      event.target.value = '';
    }
  }
  assignUserToTask(task, event) {
    const userId = event.target.value;
    if (userId) {
      this.taskService.assignUserToTask(task.id, userId).subscribe({
        next: updatedTask => {
          // Update the task in the project
          const stage = this.project.stages.find(s => s.id === task.stageId);
          if (stage) {
            const taskIndex = stage.tasks.findIndex(t => t.id === task.id);
            if (taskIndex !== -1) {
              stage.tasks[taskIndex] = updatedTask;
            }
          }
          event.target.value = '';
        },
        error: error => console.error('Error assigning user to task:', error)
      });
    }
  }
  toggleTaskCompletion(task) {
