import { Component, OnInit } from '@angular/core';
import { ProjectService } from '../../shared/services/project.service';
import { TaskService } from '../../shared/services/task.service';
import { Project, ProjectPlatform, ProjectStatus, Task } from '../../shared/models/project.model';
import { User, UserRole } from '../../shared/models/user.model';

@Component({
  selector: 'app-user-dashboard',
  templateUrl: './user-dashboard.component.html',
  styleUrls: ['./user-dashboard.component.css']
})
export class UserDashboardComponent implements OnInit {
  myProjects: Project[] = [];
  managedProjects: Project[] = [];
  selectedProject: Project | null = null;
  expandedProject: Project | null = null;

  // Stats methods
  // Only keep one correct version of each stats method below

  // Restore detectColorScheme method
  isDarkMode = false;
  toggleDarkMode() {
    this.isDarkMode = !this.isDarkMode;
    if (this.isDarkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }
  private detectColorScheme() {
    if (window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches) {
      this.isDarkMode = true;
      document.documentElement.classList.add('dark');
    }
  }
  ICONS = {
    home: `<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24' fill='none' stroke='currentColor' stroke-width='2' stroke-linecap='round' stroke-linejoin='round' class='w-5 h-5'><path d='m3 9 9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z'></path><polyline points='9 22 9 12 15 12 15 22'></polyline></svg>`,
    user: `<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24' fill='none' stroke='currentColor' stroke-width='2' stroke-linecap='round' stroke-linejoin='round' class='w-5 h-5'><path d='M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2'></path><circle cx='12' cy='7' r='4'></circle></svg>`,
    settings: `<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24' fill='none' stroke='currentColor' stroke-width='2' stroke-linecap='round' stroke-linejoin='round' class='w-5 h-5'><circle cx='12' cy='12' r='3'></circle><path d='M19.4 15a1.65 1.65 0 0 0 .33 1.82l.06.06a2 2 0 0 1 0 2.83 2 2 0 0 1-2.83 0l-.06-.06a1.65 1.65 0 0 0-1.82-.33 1.65 1.65 0 0 0-1 1.51V21a2 2 0 0 1-2 2 2 2 0 0 1-2-2v-.09A1.65 1.65 0 0 0 9 19.4a1.65 1.65 0 0 0-1.82.33l-.06.06a2 2 0 0 1-2.83 0 2 2 0 0 1 0-2.83l.06-.06a1.65 1.65 0 0 0 .33-1.82 1.65 1.65 0 0 0-1.51-1H3a2 2 0 0 1-2-2 2 2 0 0 1 2-2h.09A1.65 1.65 0 0 0 4.6 9a1.65 1.65 0 0 0-.33-1.82l-.06-.06a2 2 0 0 1 0-2.83 2 2 0 0 1 2.83 0l.06.06a1.65 1.65 0 0 0 1.82.33H9a1.65 1.65 0 0 0 1-1.51V3a2 2 0 0 1 2-2 2 2 0 0 1 2 2v.09a1.65 1.65 0 0 0 1 1.51 1.65 1.65 0 0 0 1.82-.33l.06-.06a2 2 0 0 1 2.83 0 2 2 0 0 1 0 2.83l-.06-.06a1.65 1.65 0 0 0-.33 1.82V9a1.65 1.65 0 0 0 1.51 1H21a2 2 0 0 1 2 2 2 2 0 0 1-2 2h-.09a1.65 1.65 0 0 0-1.51 1z'></path></svg>`,
    sun: `<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24' fill='none' stroke='currentColor' stroke-width='2' stroke-linecap='round' stroke-linejoin='round' class='w-5 h-5'><circle cx='12' cy='12' r='5'></circle><line x1='12' y1='1' x2='12' y2='3'></line><line x1='12' y1='21' x2='12' y2='23'></line><line x1='4.22' y1='4.22' x2='5.64' y2='5.64'></line><line x1='18.36' y1='18.36' x2='19.78' y2='19.78'></line><line x1='1' y1='12' x2='3' y2='12'></line><line x1='21' y1='12' x2='23' y2='12'></line><line x1='4.22' y1='19.78' x2='5.64' y2='18.36'></line><line x1='18.36' y1='5.64' x2='19.78' y2='4.22'></line></svg>`,
    moon: `<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24' fill='none' stroke='currentColor' stroke-width='2' stroke-linecap='round' stroke-linejoin='round' class='w-5 h-5'><path d='M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z'></path></svg>`,
    logout: `<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24' fill='none' stroke='currentColor' stroke-width='2' stroke-linecap='round' stroke-linejoin='round' class='w-5 h-5'><path d='M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4'></path><polyline points='16 17 21 12 16 7'></polyline><line x1='21' y1='12' x2='9' y2='12'></line></svg>`
  };

  // Only keep the correct versions below
  getTotalProjects(): number {
    return this.myProjects.length + this.managedProjects.length;
  }
  getCompletedProjects(): number {
    return [...this.myProjects, ...this.managedProjects].filter(p => p.status === 'completed').length;
  }
  getInProgressProjects(): number {
    return [...this.myProjects, ...this.managedProjects].filter(p => p.status === 'in_progress' || p.status === 'at_risk').length;
  }
  getLaggingProjects(): number {
    return [...this.myProjects, ...this.managedProjects].filter(p => p.status === 'lagging').length;
  }

  getPlatformDotClass(platform: ProjectPlatform): string {
    return `w-3 h-3 rounded-full ${platform === ProjectPlatform.FLAME ? 'bg-orange-500' : 'bg-blue-500'}`;
  }

  getStatusBadgeClass(status: string): string {
    const colors: { [key: string]: string } = {
      'on_track': 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300',
      'at_risk': 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-300',
      'overdue': 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300',
      'completed': 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300',
      'setup': 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300',
      'in_progress': 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300',
      'lagging': 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300'
    };
    return `px-2.5 py-0.5 text-xs font-medium rounded-full ${colors[status] || 'bg-gray-100 text-gray-800'}`;
  }

  getProgressBarClass(status: string): string {
    const colors: { [key: string]: string } = {
      'on_track': 'bg-green-500',
      'at_risk': 'bg-yellow-500',
      'overdue': 'bg-red-500',
      'completed': 'bg-green-500',
      'setup': 'bg-blue-500',
      'in_progress': 'bg-blue-500',
      'lagging': 'bg-red-500'
    };
    return `h-2 rounded-full transition-all duration-500 ${colors[status] || 'bg-gray-500'}`;
  }

  getPlatformTextClass(platform: ProjectPlatform): string {
    return platform === ProjectPlatform.FLAME ? 'text-orange-500' : 'text-blue-500';
  }

  getProjectItemClass(project: Project): string {
    const baseClasses = "p-4 border-b dark:border-gray-700 cursor-pointer transition-colors";
    const selectedClasses = this.selectedProject?.id === project.id 
      ? (project.platform === 'flame' ? 'bg-orange-500/10' : 'bg-blue-500/10')
      : 'hover:bg-gray-100 dark:hover:bg-gray-800/50';
    return `${baseClasses} ${selectedClasses}`;
  }
  projects: Project[] = [];
  userTasks: Task[] = [];
  currentUser: User = {
    id: 'dev1',
    name: 'Siddhant Salve',
    email: 'siddhant.salve@cdl.com',
    role: UserRole.LEAD_DEVELOPER,
    avatar: 'https://i.pravatar.cc/150?u=siddhant',
    isActive: true,
    createdAt: new Date()
  };

  currentView: 'home' | 'flame' | 'swayam' | 'users' | 'settings' | 'profile' | 'myProjects' = 'home';
  // Allow user management for admin, project manager, lead developer
  canManageUsers(): boolean {
    const allowedRoles = [UserRole.ADMIN, UserRole.PROJECT_MANAGER, UserRole.LEAD_DEVELOPER];
    return allowedRoles.includes(this.currentUser.role);
  }

  // Allow settings for admin and project manager
  canAccessSettings(): boolean {
    const allowedRoles = [UserRole.ADMIN, UserRole.PROJECT_MANAGER];
    return allowedRoles.includes(this.currentUser.role);
  }

  constructor(
    private projectService: ProjectService,
    private taskService: TaskService
  ) {}

  ngOnInit() {
    this.loadUserProjects();
    this.loadUserTasks();
    this.detectColorScheme();
  }

  loadUserProjects() {
    this.projectService.getAllProjects().subscribe({
      next: (projects) => {
        // For testing, show all projects. In real app, filter by user assignment
        this.projects = projects;
      },
      error: (error) => console.error('Error loading user projects:', error)
    });
  }

  loadUserTasks() {
    this.taskService.getTasksByUser(this.currentUser.id).subscribe({
      next: (tasks) => {
        this.userTasks = tasks;
      },
      error: (error) => console.error('Error loading user tasks:', error)
    });
  }

  navigateTo(view: 'home' | 'flame' | 'swayam') {
    this.currentView = view;
    this.selectedProject = null;
  }

  selectProject(project: Project) {
    this.selectedProject = project;
  }

  toggleTaskCompletion(task: Task) {
    // Implement task completion toggle logic here
    // Example:
    // task.completed = !task.completed;
    // this.taskService.updateTask(task).subscribe();
  }
}
