      next: updatedTask => {
        // Update local task
        const taskIndex = this.userTasks.findIndex(t => t.id === task.id);
        if (taskIndex !== -1) {
          this.userTasks[taskIndex] = updatedTask;
        }
        // Update project tasks if a project is selected
