    createdAt: new Date()
  };

  currentView: 'home' | 'flame' | 'swayam' | 'users' | 'settings' = 'home';
  // Allow user management for admin, project manager, lead developer
  canManageUsers(): boolean {
    const allowedRoles = [UserRole.ADMIN, UserRole.PROJECT_MANAGER, UserRole.LEAD_DEVELOPER];
    return allowedRoles.includes(this.currentUser.role);
  }

  // Allow settings for admin and project manager
  canAccessSettings(): boolean {
    const allowedRoles = [UserRole.ADMIN, UserRole.PROJECT_MANAGER];
    return allowedRoles.includes(this.currentUser.role);
  }
  selectedProject: Project | null = null;
  isDarkMode = false;

  constructor(
    private projectService: ProjectService,
    private taskService: TaskService
  ) {}

  ngOnInit() {
    this.loadUserProjects();
    this.loadUserTasks();
    this.detectColorScheme();
  }

  loadUserProjects() {
    this.projectService.getAllProjects().subscribe({
      next: (projects) => {
        // For testing, show all projects. In real app, filter by user assignment
        this.projects = projects;
      },
      error: (error) => console.error('Error loading user projects:', error)
    });
  }

  loadUserTasks() {
    this.taskService.getTasksByUser(this.currentUser.id).subscribe({
      next: (tasks) => {
        this.userTasks = tasks;
      },
      error: (error) => console.error('Error loading user tasks:', error)
    });
  }

  navigateTo(view: 'home' | 'flame' | 'swayam') {
    this.currentView = view;
    this.selectedProject = null;
  }

  selectProject(project: Project) {
    this.selectedProject = project;
  }

  toggleTaskCompletion(task: Task) {
    const newStatus = !task.isCompleted;
    this.taskService.updateTaskStatus(task.id, 
      newStatus ? 'completed' : 'on_track' as any, 
      newStatus
    ).subscribe({
      next: (updatedTask) => {
        // Update local task
        const taskIndex = this.userTasks.findIndex(t => t.id === task.id);
        if (taskIndex !== -1) {
          this.userTasks[taskIndex] = updatedTask;
        }

        // Update project tasks if a project is selected
        if (this.selectedProject) {
          this.updateProjectTask(updatedTask);
        }

        // Reload projects to update progress
        this.loadUserProjects();
      },
      error: (error) => console.error('Error updating task status:', error)
    });
  }

  private updateProjectTask(updatedTask: Task) {
    for (const stage of this.selectedProject!.stages) {
      const taskIndex = stage.tasks.findIndex(t => t.id === updatedTask.id);
      if (taskIndex !== -1) {
        stage.tasks[taskIndex] = updatedTask;
        break;
      }
    }
  }

  getStats() {
    const totalTasks = this.userTasks.length;
    const completedTasks = this.userTasks.filter(t => t.isCompleted).length;
    const overdueTasks = this.userTasks.filter(t => 
      !t.isCompleted && new Date(t.endDate) < new Date()
    ).length;
    const atRiskTasks = this.userTasks.filter(t => 
      !t.isCompleted && 
      !this.isOverdue(t) && 
      this.daysUntilDue(t) <= 7
    ).length;

    return { totalTasks, completedTasks, overdueTasks, atRiskTasks };
  }

  private isOverdue(task: Task): boolean {
    return new Date(task.endDate) < new Date() && !task.isCompleted;
  }

  private daysUntilDue(task: Task): number {
    const dueDate = new Date(task.endDate);
    const today = new Date();
    const timeDiff = dueDate.getTime() - today.getTime();
    return Math.ceil(timeDiff / (1000 * 3600 * 24));
  }

  toggleDarkMode() {
    this.isDarkMode = !this.isDarkMode;
    if (this.isDarkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }

  private detectColorScheme() {
    if (window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches) {
      this.isDarkMode = true;
      document.documentElement.classList.add('dark');
    }
  }

