import { Component, Input, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { AuthService } from '../../services/auth.service';

interface Comment {
    id: string;
    text: string;
    type: 'normal' | 'important' | 'emergency';
    user: { name: string; avatar: string };
    createdAt: string;
}

@Component({
    selector: 'app-comments',
    templateUrl: './comments.component.html',
    styleUrls: ['./comments.component.css']
})
export class CommentsComponent implements OnInit {
    @Input() projectId!: string;
    comments: Comment[] = [];
    newCommentText = '';
    newCommentType: 'normal' | 'important' | 'emergency' = 'normal';
    currentUser: any;

    constructor(private http: HttpClient, private authService: AuthService) {
        this.authService.currentUser.subscribe(user => this.currentUser = user);
    }

    ngOnInit() {
        this.loadComments();
    }

    loadComments() {
        this.http.get<Comment[]>(`http://localhost:3000/comments/project/${this.projectId}`)
            .subscribe(comments => this.comments = comments);
    }

    addComment() {
        if (!this.newCommentText.trim()) return;

        if (!this.currentUser) {
            console.error('Cannot add comment: No current user');
            return;
        }

        const payload = {
            text: this.newCommentText,
            type: this.newCommentType,
            projectId: this.projectId,
            userId: this.currentUser.id
        };

        console.log('Sending comment payload:', payload);

        this.http.post<Comment>('http://localhost:3000/comments', payload)
            .subscribe({
                next: (comment) => {
                    console.log('Comment added successfully:', comment);
                    // Ensure the user object is attached for display
                    const commentWithUser = { ...comment, user: this.currentUser };
                    this.comments.unshift(commentWithUser);
                    this.newCommentText = '';
                    this.newCommentType = 'normal';
                },
                error: (err) => console.error('Error adding comment:', err)
            });
    }

    getTypeClass(type: string): string {
        switch (type) {
            case 'normal': return 'bg-green-100 border-green-500 text-green-800';
            case 'important': return 'bg-yellow-100 border-yellow-500 text-yellow-800';
            case 'emergency': return 'bg-red-100 border-red-500 text-red-800';
            default: return 'bg-gray-50 border-gray-200 text-gray-800';
        }
    }
}
