  newUser = {
    name: '',
    email: '',
    password: '',
    role: 'user'
  };
  openUserCreationModal() {
    this.showUserCreationModal = true;
    this.newUser = {
      name: '',
      email: '',
      password: '',
      role: 'user'
    };
  }
  closeUserCreationModal() {
    this.showUserCreationModal = false;
  }
