import { Injectable } from '@angular/core';
import { CanActivate, ActivatedRouteSnapshot, RouterStateSnapshot, Router } from '@angular/router';

@Injectable({
  providedIn: 'root'
})
export class RoleGuard implements CanActivate {
  constructor(private router: Router) {}

  canActivate(
    route: ActivatedRouteSnapshot,
    state: RouterStateSnapshot
  ): boolean {
    // Get the role required for this route from the 'data' property in the routing module
    const expectedRole = route.data['role']; 
    if (!expectedRole) {
      // If no role is specified in the route, something is misconfigured. Deny access.
      console.error('[ROLE_GUARD] Route is missing the expected role in its data property.');
      return false;
    }

    // Get the current user's info from localStorage
    const userInfoString = localStorage.getItem('userInfo');
    if (!userInfoString) {
      console.error('[ROLE_GUARD] User info not found in localStorage. Redirecting to login.');
      this.router.navigate(['/auth/login']);
      return false;
    }

    // Parse the user info and get the role
    const user = JSON.parse(userInfoString);
    const userRole = user.role; // The user object from your backend has a 'role' property

    console.log(`[ROLE_GUARD] Checking access. Required role: '${expectedRole}', User's role: '${userRole}'`);

    // Compare the user's role with the expected role
    if (userRole === expectedRole) {
      console.log('[ROLE_GUARD] Role matches. Access granted.');
      return true; // The user has the correct role, allow access
    } else {
      console.error(`[ROLE_GUARD] Access DENIED. User with role '${userRole}' cannot access the '${expectedRole}' route.`);
      // If roles do not match, redirect to the main login page
      this.router.navigate(['/auth/login']); 
      return false;
    }
  }
}
