import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../../shared/services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  email = '';
  password = '';
  error = '';
  isLoading = false;

  constructor(private auth: AuthService, private router: Router) {
    console.log('[LOGIN_COMPONENT_INIT] LoginComponent initialized');
  }

  submit() {
    console.log('[LOGIN_SUBMIT_START]', { email: this.email });

    // Validate inputs
    if (!this.email || !this.email.includes('@')) {
      this.error = 'Please enter a valid email address';
      console.warn('[LOGIN_VALIDATION] Invalid email format', { email: this.email });
      return;
    }

    if (!this.password || this.password.length < 6) {
      this.error = 'Password must be at least 6 characters';
      console.warn('[LOGIN_VALIDATION] Invalid password', { passwordLength: this.password?.length || 0 });
      return;
    }

    this.error = '';
    this.isLoading = true;

    console.log('[LOGIN_API_CALL] Sending login request', { email: this.email, passwordLength: this.password.length });

    this.auth.login(this.email, this.password).subscribe({
      next: (res) => {
        console.log('[LOGIN_API_SUCCESS] Received response', {
          hasToken: !!res?.accessToken,
          hasUser: !!res?.user,
          projectCount: res?.assignedProjects?.length || 0,
          userId: res?.user?.id,
          userRole: res?.user?.role,
        });

        if (res?.accessToken) {
          try {
            // Save auth token
            localStorage.setItem('token', res.accessToken);
            console.log('[LOGIN_STORAGE] Token saved to localStorage');

            // Save user info
            localStorage.setItem('user', JSON.stringify(res.user || {}));
            console.log('[LOGIN_STORAGE] User info saved', {
              userId: res.user?.id,
              userEmail: res.user?.email,
              userRole: res.user?.role,
            });

            // Save assigned projects for quick access
            const projectsJson = JSON.stringify(res.assignedProjects || []);
            localStorage.setItem('assignedProjects', projectsJson);
            console.log('[LOGIN_STORAGE] Assigned projects saved', {
              projectCount: res.assignedProjects?.length || 0,
              projects: res.assignedProjects?.map((p: any) => ({ id: p.id, name: p.name })),
            });

            // Navigate based on role
            console.log('[LOGIN_NAVIGATE] Navigating to admin dashboard');
            this.isLoading = false;
            this.router.navigate(['/admin']);
          } catch (e) {
            console.error('[LOGIN_STORAGE_ERROR] Failed to save login info to localStorage', e);
            this.error = 'Failed to save login info. Check browser console.';
            this.isLoading = false;
          }
        } else {
          this.error = 'No access token returned from server. Check browser console.';
          console.error('[LOGIN_ERROR] Response missing accessToken', res);
          this.isLoading = false;
        }
      },
      error: (err) => {
        console.error('[LOGIN_API_ERROR] Login API error', {
          status: err?.status,
          statusText: err?.statusText,
          errorMessage: err?.error?.message,
          errorBody: err?.error,
          fullError: err,
        });

        this.error = err?.error?.message || err?.message || 'Login failed. Check browser console for details.';
        this.isLoading = false;

        // Log more context
        if (err?.status === 0) {
          console.error('[LOGIN_ERROR] Network error - backend may not be running on http://localhost:3000');
        } else if (err?.status === 401) {
          console.error('[LOGIN_ERROR] Unauthorized - invalid email or password');
        } else if (err?.status >= 500) {
          console.error('[LOGIN_ERROR] Server error - check backend logs');
        }
      }
    });
  }
}
<div class="auth-container">
  <h2>Sign In</h2>

  <div *ngIf="error" class="error">{{ error }}</div>

  <form (ngSubmit)="submit()" [attr.aria-busy]="isLoading">
    <div class="form-group">
      <label>Email</label>
      <input 
        [(ngModel)]="email" 
        name="email" 
        type="email" 
        required 
        [disabled]="isLoading"
        placeholder="Enter your email"
      />
    </div>

    <div class="form-group">
      <label>Password</label>
      <input 
        [(ngModel)]="password" 
        name="password" 
        type="password" 
        required 
        [disabled]="isLoading"
        placeholder="Enter your password"
      />
    </div>

    <button type="submit" [disabled]="isLoading">
      {{ isLoading ? 'Signing in...' : 'Sign In' }}
    </button>
  </form>

  <p>Don't have an account? <a routerLink="/auth/register" [attr.tabindex]="isLoading ? -1 : 0">Create one</a></p>
</div>
