import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Project, ProjectPlatform, ProjectStatus } from '../models/project.model';

@Injectable({
  providedIn: 'root'
})
export class ProjectService {
  private apiUrl = 'http://localhost:3000/projects';

  constructor(private http: HttpClient) { }

  getAllProjects(): Observable<Project[]> {
    return this.http.get<Project[]>(this.apiUrl);
  }

  getProjectsByPlatform(platform: ProjectPlatform): Observable<Project[]> {
    return this.http.get<Project[]>(`${this.apiUrl}/platform/${platform}`);
  }

  getProjectById(id: string): Observable<Project> {
    return this.http.get<Project>(`${this.apiUrl}/${id}`);
  }

  createProject(project: Partial<Project>): Observable<Project> {
    return this.http.post<Project>(this.apiUrl, project);
  }

  updateProject(id: string, project: Partial<Project>): Observable<Project> {
    return this.http.put<Project>(`${this.apiUrl}/${id}`, project);
  }

  deleteProject(id: string): Observable<void> {
    return this.http.delete<void>(`${this.apiUrl}/${id}`);
  }

  assignProjectManager(projectId: string, managerId: string): Observable<Project> {
    return this.http.put<Project>(`${this.apiUrl}/${projectId}/assign-manager/${managerId}`, {});
  }

  calculateProjectHealth(projectId: string): Observable<{ progress: number; status: ProjectStatus }> {
    return this.http.get<{ progress: number; status: ProjectStatus }>(`${this.apiUrl}/${projectId}/health`);
  }

  getAvailableManagers(): Observable<any[]> {
    return this.http.get<any[]>(`${this.apiUrl}/managers/available`);
  }

  addTaskToStage(stageId: string, taskData: any): Observable<any> {
    return this.http.post<any>(`${this.apiUrl}/stages/${stageId}/tasks`, taskData);
  }
  // In projects.service.ts - add template methods
getPlatformTemplates(platform: ProjectPlatform): any {
  const templates = {
    [ProjectPlatform.FLAME]: {
      stages: [
        { name: 'Pre-Production', tasks: [] },
        { name: 'Production', tasks: [] },
        { name: 'Post-Production', tasks: [] }
      ]
    },
    [ProjectPlatform.SWAYAM]: {
      stages: [
        { name: 'Production', tasks: [] },
        { name: 'Post-Production', tasks: [] }
      ]
    }
  };
  return templates[platform];
}
private createDefaultStages(projectId: string, platform: ProjectPlatform): ProjectStage[] {
  const flameStages = [
    { name: StageName.PRE_PRODUCTION, order: 1 },
    { name: StageName.PRODUCTION, order: 2 },
    { name: StageName.POST_PRODUCTION, order: 3 }
  ];

  const swayamStages = [
    { name: StageName.PRODUCTION, order: 1 },
    { name: StageName.POST_PRODUCTION, order: 2 }
  ];

  const stages = platform === ProjectPlatform.FLAME ? flameStages : swayamStages;

  return stages.map(stage => {
    const projectStage = new ProjectStage();
    projectStage.name = stage.name;
    projectStage.projectId = projectId;
    projectStage.isOpen = true;
    projectStage.progress = 0;
    projectStage.status = StageStatus.ON_TRACK;
    projectStage.tasks = [];
    projectStage.assignedTeamMembers = [];
    return projectStage;
  });
}

async create(createProjectDto: CreateProjectDto): Promise<Project> {
  const project = this.projectsRepository.create({
    ...createProjectDto,
    scenario: createProjectDto.scenario || 'New Project',
  });

  const savedProject = await this.projectsRepository.save(project);

  // Create stages based on platform template
  const stages = this.createDefaultStages(savedProject.id, savedProject.platform);
  await this.stagesRepository.save(stages);

  return this.findOne(savedProject.id);
}
}
