class AdminDashboardComponent {
  projectService;
  userService;
  projects = [];
  filteredProjects = [];
  selectedPlatform = 'all';
  currentView = 'home';
  selectedProject = null;
  isDarkMode = false;
  availableManagers = [];
  showTaskModal = false;
  selectedStageId = '';
  // track which admin section is active
  currentSection = 'dashboard';
  constructor(projectService, userService) {
    this.projectService = projectService;
    this.userService = userService;
  }
  ngOnInit() {
    this.loadProjects();
    this.loadAvailableManagers();
    this.detectColorScheme();
  }
  loadProjects() {
    this.projectService.getAllProjects().subscribe({
      next: projects => {
        this.projects = projects;
        this.filterProjects();
      },
      error: error => console.error('Error loading projects:', error)
    });
  }
  loadAvailableManagers() {
    this.projectService.getAvailableManagers().subscribe({
      next: managers => {
        this.availableManagers = managers;
      },
      error: error => console.error('Error loading managers:', error)
    });
  }
  filterProjects() {
    if (this.selectedPlatform === 'all') {
      this.filteredProjects = this.projects;
    } else {
      this.filteredProjects = this.projects.filter(p => p.platform === this.selectedPlatform);
    }
  }
  navigateTo(view) {
    this.currentView = view;
    if (view === 'home') {
      this.selectedPlatform = 'all';
    } else {
      this.selectedPlatform = view;
    }
    this.filterProjects();
    this.selectedProject = null;
  }
  navigateToSection(section) {
    this.currentSection = section;
  }
  selectProject(project) {
    this.selectedProject = project;
  }
  createNewProject(platform) {
    const newProject = {
      name: 'New Untitled Project',
      platform: platform,
      scenario: 'Awaiting Setup'
    };
    this.projectService.createProject(newProject).subscribe({
      next: project => {
        this.projects.unshift(project);
        this.selectedProject = project;
        this.filterProjects();
      },
      error: error => console.error('Error creating project:', error)
    });
  }
  deleteProject(projectId) {
    if (confirm('Are you sure you want to delete this project?')) {
      this.projectService.deleteProject(projectId).subscribe({
        next: () => {
          this.projects = this.projects.filter(p => p.id !== projectId);
          if (this.selectedProject?.id === projectId) {
            this.selectedProject = null;
          }
          this.filterProjects();
        },
        error: error => console.error('Error deleting project:', error)
      });
    }
  }
  assignProjectManager(projectId, managerId) {
    if (managerId) {
      this.projectService.assignProjectManager(projectId, managerId).subscribe({
        next: updatedProject => {
          const index = this.projects.findIndex(p => p.id === projectId);
          if (index !== -1) {
            this.projects[index] = updatedProject;
          }
          if (this.selectedProject?.id === projectId) {
            this.selectedProject = updatedProject;
          }
        },
        error: error => console.error('Error assigning manager:', error)
      });
    }
  }
  getStatusColor(status) {
    const colors = {
      [ProjectStatus.SETUP]: 'blue',
      [ProjectStatus.IN_PROGRESS]: 'blue',
      [ProjectStatus.AT_RISK]: 'yellow',
      [ProjectStatus.LAGGING]: 'red',
      [ProjectStatus.COMPLETED]: 'green'
    };
    return colors[status] || 'gray';
  }
  getStats() {
    const total = this.projects.length;
    const completed = this.projects.filter(p => p.status === ProjectStatus.COMPLETED).length;
    const inProgress = this.projects.filter(p => [ProjectStatus.IN_PROGRESS, ProjectStatus.AT_RISK].includes(p.status)).length;
    const lagging = this.projects.filter(p => p.status === ProjectStatus.LAGGING).length;
    return {
      total,
      completed,
      inProgress,
      lagging
    };
  }
  toggleDarkMode() {
    this.isDarkMode = !this.isDarkMode;
    if (this.isDarkMode) {
