import { Component, OnInit } from '@angular/core';
import { ProjectService } from '../../shared/services/project.service';
import { TaskService } from '../../shared/services/task.service';
import { Project, ProjectPlatform, ProjectStatus } from '../../shared/models/project.model';
import { Task } from '../../shared/models/project.model';
import { User } from '../../shared/models/user.model';
import { Router } from '@angular/router';
import { DomSanitizer, SafeHtml } from '@angular/platform-browser';

@Component({
  selector: 'app-user-dashboard',
  templateUrl: './user-dashboard.component.html',
  styleUrls: ['./user-dashboard.component.css']
})
export class UserDashboardComponent implements OnInit {
  projects: Project[] = [];
  myProjects: Project[] = [];
  managedProjects: Project[] = [];
  userTasks: Task[] = [];
  currentUser: User | null = null;
  selectedProject: Project | null = null;
  expandedProject: Project | null = null;
  isDarkMode = false;
  currentView: 'home' | 'myProjects' | 'profile' | 'settings' = 'home';

  ICONS = {
    home: '<svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-5 h-5"><path stroke-linecap="round" stroke-linejoin="round" d="M2.25 12l8.954-8.955c.44-.439 1.152-.439 1.591 0L21.75 12M4.5 9.75v10.125c0 .621.504 1.125 1.125 1.125H9.75v-4.875c0-.621.504-1.125 1.125-1.125h2.25c.621 0 1.125.504 1.125 1.125V21h4.125c.621 0 1.125-.504 1.125-1.125V9.75M8.25 21h8.25" /></svg>',
    user: '<svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-5 h-5"><path stroke-linecap="round" stroke-linejoin="round" d="M15.75 6a3.75 3.75 0 11-7.5 0 3.75 3.75 0 017.5 0zM4.501 20.118a7.5 7.5 0 0114.998 0A17.933 17.933 0 0112 21.75c-2.676 0-5.216-.584-7.499-1.632z" /></svg>',
    settings: '<svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-5 h-5"><path stroke-linecap="round" stroke-linejoin="round" d="M9.594 3.94c.09-.542.56-.94 1.11-.94h2.593c.55 0 1.02.398 1.11.94l.213 1.281c.063.374.313.686.645.87.074.04.147.083.22.127.324.196.72.257 1.075.124l1.217-.456a1.125 1.125 0 011.37.49l1.296 2.247a1.125 1.125 0 01-.26 1.431l-1.003.827c-.293.24-.438.613-.431.992a6.759 6.759 0 010 .255c-.007.378.138.75.43.99l1.005.828c.424.35.534.954.26 1.43l-1.298 2.247a1.125 1.125 0 01-1.369.491l-1.217-.456c-.355-.133-.75-.072-1.076.124a6.57 6.57 0 01-.22.128c-.331.183-.581.495-.644.869l-.212 1.28c-.09.543-.56.941-1.11.941h-2.594c-.55 0-1.02-.398-1.11-.94l-.213-1.281c-.062-.374-.312-.686-.644-.87a6.52 6.52 0 01-.22-.127c-.325-.196-.72-.257-1.076-.124l-1.217.456a1.125 1.125 0 01-1.369-.49l-1.297-2.247a1.125 1.125 0 01.26-1.431l1.004-.827c.292-.24.437-.613.43-.992a6.932 6.932 0 010-.255c.007-.378-.138-.75-.43-.99l-1.004-.828a1.125 1.125 0 01-.26-1.43l1.297-2.247a1.125 1.125 0 011.37-.491l1.216.456c.356.133.751.072 1.076-.124.072-.044.146-.087.22-.128.332-.183.582-.495.644-.869l.214-1.281z" /><path stroke-linecap="round" stroke-linejoin="round" d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" /></svg>',
    logout: '<svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-5 h-5"><path stroke-linecap="round" stroke-linejoin="round" d="M15.75 9V5.25A2.25 2.25 0 0013.5 3h-6a2.25 2.25 0 00-2.25 2.25v13.5A2.25 2.25 0 007.5 21h6a2.25 2.25 0 002.25-2.25V15M12 9l-3 3m0 0l3 3m-3-3h12.75" /></svg>',
    sun: '<svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-5 h-5"><path stroke-linecap="round" stroke-linejoin="round" d="M12 3v2.25m6.364.386l-1.591 1.591M21 12h-2.25m-.386 6.364l-1.591-1.591M12 18.75V21m-4.773-4.227l-1.591 1.591M5.25 12H3m4.227-4.773L5.636 5.636M15.75 12a3.75 3.75 0 11-7.5 0 3.75 3.75 0 017.5 0z" /></svg>',
    moon: '<svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-5 h-5"><path stroke-linecap="round" stroke-linejoin="round" d="M21.752 15.002A9.718 9.718 0 0118 15.75c-5.385 0-9.75-4.365-9.75-9.75 0-1.33.266-2.597.748-3.752A9.753 9.753 0 003 11.25C3 16.635 7.365 21 12.75 21a9.753 9.753 0 009.002-5.998z" /></svg>'
  };

  constructor(
    private projectService: ProjectService,
    private taskService: TaskService,
    private router: Router,
    private sanitizer: DomSanitizer
  ) { }

  ngOnInit() {
    this.loadCurrentUser();
    this.detectColorScheme();
    if (this.currentUser) {
      this.loadUserTasks();
      this.loadUserProjects();
    } else {
      this.router.navigate(['/auth/login']);
    }
  }

  loadCurrentUser() {
    const userInfoString = localStorage.getItem('userInfo');
    if (userInfoString) {
      this.currentUser = JSON.parse(userInfoString) as User;
    } else {
      console.error("UserDashboard: Could not find user info in localStorage.");
    }
  }

  loadUserProjects() {
    if (!this.currentUser) return;

    this.projectService.getAssignedProjects(this.currentUser.id).subscribe({
      next: (projects) => {
        this.projects = projects;
        // Split projects into managed and assigned
        this.managedProjects = projects.filter(p => p.projectManagerId === this.currentUser?.id || (p.projectManager && p.projectManager.id === this.currentUser?.id));
        this.myProjects = projects.filter(p => p.projectManagerId !== this.currentUser?.id && (!p.projectManager || p.projectManager.id !== this.currentUser?.id));

        console.log('Loaded projects:', { total: projects.length, managed: this.managedProjects.length, my: this.myProjects.length });
      },
      error: (error) => console.error('Error loading user projects:', error)
    });
  }

  loadUserTasks() {
    if (!this.currentUser) return;

    this.taskService.getTasksByUser(this.currentUser.id).subscribe({
      next: (tasks) => {
        this.userTasks = tasks;
        console.log('Successfully loaded user tasks:', tasks);
      },
      error: (error) => console.error('Error loading user tasks:', error)
    });
  }

  navigateTo(view: 'home' | 'myProjects' | 'profile' | 'settings') {
    this.currentView = view;
  }

  selectProject(project: Project) {
    this.selectedProject = project;
    this.expandedProject = project;
  }

  closeExpandedProject() {
    this.expandedProject = null;
  }

  isManager(project: Project): boolean {
    return project.projectManagerId === this.currentUser?.id || (project.projectManager && project.projectManager.id === this.currentUser?.id);
  }

  toggleDarkMode() {
    this.isDarkMode = !this.isDarkMode;
    if (this.isDarkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }

  private detectColorScheme() {
    if (window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches) {
      this.isDarkMode = true;
      document.documentElement.classList.add('dark');
    }
  }

  getPlatformDotClass(platform: ProjectPlatform): string {
    return `w-3 h-3 rounded-full ${platform === ProjectPlatform.FLAME ? 'bg-orange-500' : 'bg-blue-500'}`;
  }

  getStatusBadgeClass(status: ProjectStatus): string {
    const colors = {
      [ProjectStatus.SETUP]: 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300',
      [ProjectStatus.IN_PROGRESS]: 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300',
      [ProjectStatus.AT_RISK]: 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-300',
      [ProjectStatus.LAGGING]: 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300',
      [ProjectStatus.COMPLETED]: 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300'
    };
    return `px-2.5 py-0.5 text-xs font-medium rounded-full ${colors[status] || 'bg-gray-100 text-gray-800'}`;
  }

  getProgressBarClass(status: ProjectStatus): string {
    const colors = {
      [ProjectStatus.SETUP]: 'bg-blue-500',
      [ProjectStatus.IN_PROGRESS]: 'bg-blue-500',
      [ProjectStatus.AT_RISK]: 'bg-yellow-500',
      [ProjectStatus.LAGGING]: 'bg-red-500',
      [ProjectStatus.COMPLETED]: 'bg-green-500'
    };
    return `h-2 rounded-full transition-all duration-500 ${colors[status] || 'bg-gray-500'}`;
  }

  getIcon(name: string): SafeHtml {
    // @ts-ignore
    return this.sanitizer.bypassSecurityTrustHtml(this.ICONS[name] || '');
  }
}
